import React, { Component } from 'react';
import axios from 'axios';
const input = {
  border : '1px solid #2A7E7C',
  margin: '10px',
  width: '225px',
  height: '40px'       
}
const btn = {
  width: '200px',
  height: '50px',
  margin: '0 auto'
}
const imageBackground = 'https://media.gettyimages.com/photos/sharing-food-picture-id825855446?s=612x612'
export default class Input extends Component {
  state={
    form:{
      namefood :"",
      amount:"",
      description:"",
      location:"",
      file: '',
      imageUrl: 'data:image/png;base64,',
      booking: true}
    };


clean=()=>{
      this.setState({
        form:{
        ...this.state.form, 
        namefood :"",
        amount:"",
        description:"",
        location:"",
        file: '',
        imageUrl: 'data:image/png;base64,',
        booking: true
      }
      })
    }
    
handleInput = e => {
        let name = e.target.name
        let value = e.target.value
        if(name === "location"){
          this.state.form.location = e.target.value
        }else{
         this.setState({form: { 
           ...this.state.form,
          [name] : value}
        })
      }
      console.log('this.state', this.state)
    }

Share= (newPost,clean) => {
            axios.post(`http://localhost:9000/post/post`,newPost)
            .then(res => {
              console.log('res.data', res.data)
              this.setState({form: res.data})
              console.log('this.state', this.state)})
            .catch(err => {
              console.log(err);
            })
            clean()
          }

handleImageChange = (e) => {
        e.preventDefault();
        let reader = new FileReader();
        let file = e.target.files[0];
        console.log(file)
        reader.onloadend = () => {
            this.setState({form: { 
              ...this.state.form,
              file: file.name,
              imageUrl: reader.result}
           })        }
        reader.readAsDataURL(file)
      }

render() {
      let {imageUrl} = this.state.form;
      let $image = null;
      if (imageUrl) {
        $image = (<img alt="" style={{border:'1px solid #2A7E7C',borderRadius: '5px ', width: '200px', height: '200px', margin: '20px'}} src={imageUrl} />);
      } else {
        $image = (<img alt="" style={{border:'1px solid #2A7E7C',borderRadius: '5px ', width: '200px', height: '200px', margin: '20px'}} src='https://wolper.com.au/wp-content/uploads/2017/10/image-placeholder.jpg'></img>);
      }
        return (
        <div style={{
          border: 'solid 1px grey',
          borderRadius: '7px',
          margin: '0 auto',
          height: '450px',
          display: 'grid',
          padding: '30px',
          backgroundImage: `url(${imageBackground})`,
          gridTemplateColumns: '1fr'}}>
         
         <div  style={{
              padding: "25px",
              gridColumn: '1/2',
              display: 'grid',
              justifyContent: 'space-around',
              gridTemplateColumns: '1fr 1fr',

            }}>
         
          <form
          onSubmit={this.handleInput}
          >

          <div 
            style={{
              gridColumn: '2/3', 
              display: 'grid', 
              justifyContent: 'center',
              backgroundColor: '#1D6161',
              borderRadius: '10px',
              opacity: '0.7', 
              width: '70%'
              }}>
            <input
              type="text"
              placeholder="Food Type"
              name="namefood"
              onChange={this.handleInput}
              style={input}
              value={this.state.form.namefood}
            />
            <input
              type="text"
              placeholder= "Amount"
              onChange={this.handleInput}
              name="amount"
              style={input}
              value={this.state.form.amount}

            />
             <input
              type="text"
              placeholder="Brief Description"
              onChange={this.handleInput}
              name="description"
              style={input}
              value={this.state.form.description}

            />
            <select
              style={input}
              onChange={this.handleInput}
              className="custom-select mr-sm-2"
              name="location"

            >
              <option name='select' >Select country</option>
              <option name='Irbid' >Irbid</option>
              <option name='Jarash' >Jarash</option>
              <option name='Az-Zarqa' >Az-Zarqa</option>
              <option name='Tafelh' >Tafelh</option>
              <option name='Ajloun' >Ajloun</option>
              <option name='Aqaba' >Aqaba</option>
              <option name='Amman' >Amman</option>
              <option name='Karak' >Karak</option>
              <option name='Madba' >Madba</option>
              <option name='Ma`an' >Ma`an</option>
              <option name='Mafraq' >Mafraq</option>
              <option name='Jarash' >Jarash</option>
            </select>
        
              <button
                style={{width: '100px',
                height: '80px',
                margin: '0 auto'}}
                className="btn btn-success btn-lg"
                type="submit"
                onClick={this.Share.bind(this,this.state.form,this.clean)}>
                Share
              </button>
          </div>
​        </form> 

          <div
            style={{
              gridColumn: '1/2', 
              gridRow: '1', 
              display: 'grid', 
              justifyContent: 'center',
              backgroundColor: '#1D6161',
              borderRadius: '10px',
              opacity: '0.7',
              padding: '30px',
              width: '70%'}}
          >
            <input
              style={{display: 'none'}}
              type="file" 
              onChange={(e)=>this.handleImageChange(e)}
              ref={input => this.input = input}/>
            {$image}
          <button
            style={btn}
           className="btn btn-danger btn-lg"
           onClick={()=>this.input.click()}>Upload Image
          </button>
          </div>  
          </div>        
            </div>
        )
    }
}